{
  "type": "website",
  "configId": {{configId}},
  "configVersion": 1,
  "defaultFile": "NO_MATCH",
  "effectiveSecurityControls": {
    "applyApplicationLayerControls": true,
    "applyNetworkLayerControls": true,
    "applyRateControls": true,
    "applySlowPostControls": true
  },
  "filePaths": [
    "/*"
  ],
  "hostnames": [
    "{{hostnameToAdd}}"
  ],
  "isNegativeFileExtensionMatch": false,
  "isNegativePathMatch": true,
  "securityPolicy": {
    "policyId": "{{policyId}}"
  }
}

{
  "type": "website",
  "configId": {{configId}},
  "configVersion": 1,
  "defaultFile": "NO_MATCH",
  "effectiveSecurityControls": {
    "applyApplicationLayerControls": true,
    "applyNetworkLayerControls": true,
    "applyRateControls": true,
    "applySlowPostControls": true
  },
  "filePaths": [
    "/*"
  ],
  "hostnames": [
    "{{hostnameToAdd}}"
  ],
  "isNegativeFileExtensionMatch": false,
  "isNegativePathMatch": true,
  "securityPolicy": {
    "policyId": "{{policyId}}"
  },
  "sequence": 1,
  "targetId": {{targetId}}
}

{
    "matchType": "path",
    "type": "WAF",
    "name": "Test_Paths 2",
    "description": "AFW Test Extensions",
    "averageThreshold": 5,
    "burstThreshold": 10,
    "clientIdentifier": "ip",
    "useXForwardForHeaders": true,
    "requestType": "ClientRequest",
    "sameActionOnIpv6": false,
    "path": {
        "positiveMatch": true,
        "values": [
            "/login/",
            "/path/"
        ]
    },
    "pathMatchType": "Custom",
    "pathUriPositiveMatch": true,
    "fileExtensions": {
        "positiveMatch": false,
        "values": [
            "3g2",
            "3gp",
            "aif",
            "aiff",
            "au",
            "avi",
            "bin",
            "bmp",
            "cab"
        ]
    },
    "hostnames": [
        "www.ludin.org"
    ],
    "additionalMatchOptions": [
        {
            "positiveMatch": true,
            "type": "IpAddressCondition",
            "values": [
                "198.129.76.39"
            ]
        },
        {
            "positiveMatch": true,
            "type": "RequestMethodCondition",
            "values": [
                "GET"
            ]
        }
    ],
    "queryParameters": [
        {
            "name": "productId",
            "values": [
                "BUB_12",
                "SUSH_11"
            ],
            "positiveMatch": true,
            "valueInRange": false
        }
    ]
}

