            "rules": [
                // use LLDP attributes
                {
                    "src": "lldp_system_name", // or lldp_chassis_id
                    "expression": "[0:3]", // "abcdef" -> "abc"
                    // "a.b.c" -> "b"
                    // "a1234-b5678-c90" -> "b56"
                    "equals": "SENSOR",
                    "usage": "iot"
                },
                // use equals_any to match any item in a list
                {
                    "src": "lldp_chassis_id",
                    "expression": "[0:17]",
                    "equals_any": [
                        "00:11:22:33:44:55",
                        "00:11:22:33:44:66”, “00:11:22:33:44:77"
                    ],
                    "usage": "iot"
