query opportunitiesClosingSoon($month: Long) {
  uiapi {
    query {
      Opportunity(
        where: {
          StageName: { eq: "Negotiation/Review" }
          CloseDate: { CALENDAR_MONTH: { value: { eq: $month } } }
        }
      ) {
        edges {
          node {
            Id
            Name {
              value
